import React, { useState } from "react";
import useForm from "./hooks";

import WeatherMeta from "../../api/weathermeta"


const Form = () => {
    const {inputs, handleInputChange, handleSubmit} = useForm(WeatherMeta.getLocation(inputs.zipcode));

    return (
        <form onSubmit={handleSubmit}>
            <label htmlFor="zipcode"></label>
            <input onChange={handleInputChange} id="location" name="location" type="text" placeholder="Enter the name of your city." value={inputs.location}></input>
            <button type="submit">Go</button>

        </form>
    )
};

export default Form;
