import "./style.scss";

// Component Imports
import Header from "../Header";
import Main from "../Main";

const Layout = () => {
  const [weather, setWeather] = useState({});
  const { inputs, handleInputChange, handleSubmit } = useForm(MetaWeather.getLocation);

  useEffect(() => console.log("Weather: ", weather));

  return (
    <div>
      <Header />
      <Main setWeather={setWeather} weather={weather} />
    </div>
  );
};

export default Layout;
