import axios from "axios";
import { rootUrl } from "../../utils";

class MetaWeather {
  constructor() {
    this.state = {
      woeid: null
    };
  }

  getLocation = async query => {
    let response = await axios.get(`${rootUrl}/api/location/search/${query}`, {crossdomain: true})
    if (response.data) {
      this.state.woeid = response.data[0].woeid;
    }
    console.log(response);
}
}
const MW = new MetaWeather();
export {MW as MetaWeather};
