import React from "react";
import useForm from "./hooks";

import MetaWeather from "../../api/metaweather";
const MW = new MetaWeather();


const Form = () => {
    const {inputs, handleInputChange, handleSubmit} = useForm(MetaWeather.getLocation);
    MW.getLocation("ahh");

    return (
        <form onSubmit={handleSubmit}>
            <label htmlFor="location"></label>
            <input onChange={handleInputChange} id="location" name="location" type="text" placeholder="Enter the name of your city." value={inputs.location}></input>
            <button type="submit">Go</button>

        </form>
    )
};

export default Form;
