import axios from "axios";
import { rootUrl } from "../../utils";

class MetaWeather {
  constructor() {
    this.state = {
      woeid: null
    };
  }

  getLocation = async query => {
    let locationResponse = await axios.get(`${rootUrl}/api/location/search/${query}`)
    if (locationResponse.data) {
      this.state.woeid = locationResponse.data[0].woeid;
    }
}
}
const MW = new MetaWeather();
export {MW as MetaWeather};
