import axios from "axios";
import { rootUrl } from "../../utils";

class MetaWeather {
  constructor() {
    this.state = {
      woeid: null
    };
  }

  getLocation = async query => {
    let response = await axios.get(`${rootUrl}/api/location/${query}`, {crossdomain: true})
    if (response.data) {
      this.woeid = response.data.woeid;
    }
}
}
const MW = new MetaWeather();
export {MW as MetaWeather};
